import data from "./goods.json";

function GoodsRender() {
    return (
        <div className="goods">
            <div className="goods_title">
                <h1>Популярные товары</h1>
            </div>
            <div className="goods_wrapper">
                {data.map((item, index) => (
                    <div className="card" key={index}>
                        <img
                            className="goods_img"
                            src={item.img}
                            alt={item.name}
                        />
                        <div className="goods_text">
                            <h3 className="goods_name">{item.name}</h3>
                            {item.model ? (
                                <p className="goods_model">
                                    Модель: {item.model}
                                </p>
                            ) : item.color ? (
                                <p className="goods_color">
                                    Цвет: {item.color}
                                </p>
                            ) : item.type ? (
                                <p className="goods_type">Тип: {item.type}</p>
                            ) : item.brand ? (
                                <p className="goods_brand">
                                    Бренд: {item.brand}
                                </p>
                            ) : item.author ? (
                                <p className="goods_author">
                                    Автор: {item.author}
                                </p>
                            ) : item.size ? (
                                <p className="goods_size">
                                    Размер: {item.size}
                                </p>
                            ) : item.title ? (
                                <p className="goods_title">
                                    Название: {item.title}
                                </p>
                            ) : (
                                <p className="goods_style">
                                    Стиль: {item.style}
                                </p>
                            )}
                            <p className="goods_price">Цена: {item.price} тг.</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GoodsRender;
